import {Box, Heading, Text} from "ferns-ui";
import React from "react";

import {DemoConfiguration} from "./src/demoConfig";

// comment
// :
// {summary: Array(1)}
// flags
// :
// isOptional
// :
// true
// [[Prototype]]
// :
// Object
// id
// :
// 719
// kind
// :
// 1024
// kindString
// :
// "Property"
// name
// :
// "avatarImageWidth"
// sources
// :
// [{â€¦}]
// type
// :
// {type: 'intrinsic', name: 'number'}

const ComponentProps = ({props}: {props: any}) => {
  return (
    <Box>
      {props.map((p) => (
          <>
        <Text key={p.name}>
          {p.name} {p.flags?.isOptional !== true ? "(Required)" : ""}
        </Text>
      {Boolean(p.comment?.summary) && <Text>{p.comment?.summary}</Text>}
          </>
      ))}
    </Box>
  );
};

export const ComponentPage = ({config}: {config: DemoConfiguration}): React.ReactElement => {
  console.log("PROPS", config, (config as any).props);
  return (
    <Box>
      <Heading>{config.name}</Heading>
      <Heading size="sm">Description</Heading>
      <Text>{config.description}</Text>
      {config.demo?.()}
      <ComponentProps props={config.props?.children} />
      <Box>
        {/* TODO: make these checkboxes with tooltips */}
        <Text>Documentation: {config.status.documentation}</Text>
        <Text>Figma: {config.status.documentation}</Text>
        <Text>Web: {config.status.web}</Text>
        <Text>iOS: {config.status.ios}</Text>
        <Text>Android: {config.status.android}</Text>
      </Box>
      {Boolean(config.related.length) && (
        <Box>
          <Text weight="bold">Related:</Text>
          <Text>{config.related}</Text>
        </Box>
      )}
      {/* {config.stories?.map((s, i): any => <View key={i}>s()</View>)} */}
    </Box>
  );
};
